angular.module('ionicApp.services', [])

/**
 * A simple EXAMPLE!!!!!!!! service that returns some data.
 */
    .factory('moviesService', function() {
      // Might use a resource here that returns a JSON array

      // Some fake testing data
      var movies = [
        {
          id: 0,
          title: 'Jurassic World',
          description: '22 años después de los eventos ocurridos en Jurassic Park, la Isla Nublar se prepara para recibir a los visitantes de Jurassic World, el parque temático de dinosaurios que originalmente había sido diseñado por John Hammond. El parque trae toda una serie de eventos: shows acuáticos, carreras y más. Hasta que un experimento biológico sale mal.',
          image: 'data:image/jpeg;base64,'
        },
        {
          id: 1,
          title: 'Beatles',
          description: 'Basado en el libro del escritor noruego Lars Christensen Saabye. En donde cuatro amigos crecidos en Oslo, en los años sesenta, descubren junto al mundo el fenómeno cultural: The Beatles. De esta manera deciden formar su propia banda que se llamará "The Snafus".',
          image: 'data:image/jpeg;base64,'
        },
        {
          id: 2,
          title: 'Terminator Génesis',
          description: 'El año es 2029, John Connor, lider de la resistencia continua la guerra contra las máquinas. En la base de Los Ángeles, los peores miedos de John sobre el futuro se vuelven realidad, cuando descubre que SkyNet planeta un nuevo ataque desde ambos frentes: el pasado y el futuro, que podrán cambiar la guerra para siempre. ',
          image: 'data:image/jpeg;base64,'
        }
      ];

      return {
        all: function() {
          return movies;
        },
        get: function(movieId) {
          return movies[movieId];
        },
        addMovie: function(movie) {
          movies.push(movie);
        },
        deleteMovie: function(movie) {
          for (var i = 0; i < movies.length; i++) {
            if(movies[i].id == movie.id) {
              movies.splice(i, 1);
              break;
            }
          }
        },
        modifyMovie: function(movie) {
          for (var i = 0; i < movies.length; i++) {
            if(movies[i].id == movie.id) {
              movies[i] = movie;
            }
          }
        }
      };
    })

    .directive('ionSearch', function() {
        return {
            restrict: 'E',
            replace: true,
            scope: {
                getData: '&source',
                model: '=?',
                search: '=?filter'
            },
            link: function(scope, element, attrs) {
                attrs.minLength = attrs.minLength || 0;
                scope.placeholder = attrs.placeholder || '';
                scope.search = {value: ''};
                if (attrs.class)
                    element.addClass(attrs.class);

                if (attrs.source) {
                    scope.$watch('search.value', function (newValue, oldValue) {
                        if (newValue.length > attrs.minLength) {
                            scope.getData({str: newValue}).then(function (results) {
                                scope.model = results;
                            });
                        } else {
                            scope.model = [];
                        }
                    });
                }

                scope.clearSearch = function() {
                    scope.search.value = '';
                };
            },
            template: '<div class="item-input-wrapper">' +
                        '<i class="icon ion-android-search"></i>' +
                        '<input type="search" placeholder="{{placeholder}}" ng-model="search.value">' +
                        '<i ng-if="search.value.length > 0" ng-click="clearSearch()" class="icon ion-close"></i>' +
                      '</div>'
        };
    });